        // üìù Add a new Note + Log into LeadTimeline
        public async Task<NoteDto> AddNoteAsync(Guid businessId, NoteDto dto)
        {
            // 1Ô∏è‚É£ Map incoming DTO to Note entity
            var note = NoteMapper.MapToEntity(dto, businessId);

