using System;
using System.Threading;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.RateLimiting;
using Microsoft.Extensions.Logging;
using xbytechat.api.Features.ESU.Facebook.Abstractions;
using xbytechat.api.Features.ESU.Facebook.DTOs;
using xbytechat.api.Features.ESU.Facebook.Services;
using xbytechat.api.Shared;

namespace xbytechat.api.Features.ESU.Facebook.Controllers
{
    [ApiController]
    [Route("api/esu/facebook")]
    [Authorize] // all endpoints below require authenticated workspace
    public sealed class FacebookEsuController : ControllerBase
    {
        private readonly IFacebookEsuService _service;
        private readonly IEsuStatusService _status;
        private readonly ILogger<FacebookEsuController> _log;
        private readonly string _uiBase;

        public FacebookEsuController(
            IFacebookEsuService service,
            IEsuStatusService status,
            ILogger<FacebookEsuController> log,
            IConfiguration cfg)
        {
            _service = service;
            _status = status;
            _log = log;
            _uiBase = (cfg["Ui:PublicBaseUrl"] ?? "http://localhost:3000/").TrimEnd('/');
        }

        [HttpGet("health")]
        [AllowAnonymous]
        public IActionResult Health()
            => Ok(new { module = "ESU", provider = "FACEBOOK", status = "OK" });

        // -------- START / RESTART ESU --------

        [HttpPost("start")]
        [DisableRateLimiting] // ESU handshake must not be throttled
        public async Task<IActionResult> Start(
            [FromBody] FacebookEsuStartRequestDto? dto,
            CancellationToken ct)
        {
            try
            {
                var businessId = User.GetBusinessId();
                if (businessId == Guid.Empty)
                    return Unauthorized(new { ok = false, message = "Business context missing in token." });

                var res = await _service.StartAsync(businessId, dto?.ReturnUrlAfterSuccess, ct);

                _log.LogInformation(
                    "ESU start issued for business={BusinessId}, state={State}, expires={Expires}",
                    businessId, res.State, res.ExpiresAtUtc);

                return Ok(new
                {
                    ok = true,
                    data = new
                    {
                        authUrl = res.LaunchUrl,
                        state = res.State,
                        expiresAtUtc = res.ExpiresAtUtc
                    }
                });
            }
            catch (Exception ex)
            {
                _log.LogError(ex, "ESU start failed.");
                return StatusCode(500, new { ok = false, message = "Failed to start Meta Embedded Signup." });
            }
        }

        // -------- OAUTH CALLBACK (PUBLIC) --------

        [HttpGet("callback")]
        [AllowAnonymous]
        [DisableRateLimiting]
        public async Task<IActionResult> Callback(
            [FromQuery] string? code,
            [FromQuery] string? state,
            CancellationToken ct)
        {
            Response.Headers["Cache-Control"] = "no-store, no-cache, must-revalidate, max-age=0";
            Response.Headers["Pragma"] = "no-cache";

            string Target(string q) => $"{_uiBase}/app/welcomepage{q}";

            if (string.IsNullOrWhiteSpace(code) || string.IsNullOrWhiteSpace(state))
            {
                _log.LogWarning("ESU callback missing parameters. codeNull={CodeNull} stateNull={StateNull}",
                    string.IsNullOrWhiteSpace(code), string.IsNullOrWhiteSpace(state));
                return Redirect(Target("?error=missing_code_or_state"));
            }

            try
            {
                await _service.HandleCallbackAsync(code!, state!, ct);
                _log.LogInformation("ESU callback success for state={State}", state);
                return Redirect(Target("?connected=1"));
            }
            catch (Exception ex)
            {
                _log.LogError(ex, "ESU callback failed for state={State}", state);
                return Redirect(Target("?error=oauth_exchange_failed"));
            }
        }

        // -------- DISCONNECT (FULL DEAUTHORIZE) --------

        [HttpDelete("disconnect")]
        public async Task<IActionResult> Disconnect(CancellationToken ct)
        {
            try
            {
                var businessId = User.GetBusinessId();
                if (businessId == Guid.Empty)
                    return Unauthorized(new { ok = false, message = "Business context missing in token." });

                await _service.DisconnectAsync(businessId, ct);
                return Ok(new { ok = true });
            }
            catch (Exception ex)
            {
                _log.LogError(ex, "ESU disconnect failed.");
                return StatusCode(500, new
                {
                    ok = false,
                    message = "Failed to disconnect WhatsApp for this workspace."
                });
            }
        }

        // -------- STATUS --------

        [HttpGet("status")]
        public async Task<IActionResult> GetStatus(CancellationToken ct)
        {
            var businessId = User.GetBusinessId();
            if (businessId == Guid.Empty)
                return Unauthorized(new { ok = false, message = "Business context missing in token." });

            var dto = await _status.GetStatusAsync(businessId, ct);

            // FE already supports both plain DTO and { ok, data }
            return Ok(new { ok = true, data = dto });
        }

        // inside FacebookEsuController

        [HttpDelete("hard-delete-full-account")]
        public async Task<IActionResult> DeleteAccountAndData(CancellationToken ct)
        {
            try
            {
                var businessId = User.GetBusinessId();
                if (businessId == Guid.Empty)
                    return Unauthorized(new { ok = false, message = "Business context missing in token." });

                await _service.FullDeleteAsync(businessId, ct);

                return Ok(new
                {
                    ok = true,
                    message = "WhatsApp Business API connection and related onboarding data have been deleted for this workspace."
                });
            }
            catch (Exception ex)
            {
                _log.LogError(ex, "ESU account/data delete failed.");
                return StatusCode(500, new
                {
                    ok = false,
                    message = "Failed to delete WhatsApp onboarding data for this workspace."
                });
            }
        }

    }
}

#nullable enable
using System;
using System.Collections.Generic; // <-- needed for the /me call
using System.Linq;
using System.Threading;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Logging;
using xbytechat.api.Features.ESU.Facebook.Abstractions;
using xbytechat.api.Features.ESU.Facebook.DTOs;
using xbytechat.api.Infrastructure;
using xbytechat.api.Shared; // AppDbContext

namespace xbytechat.api.Features.ESU.Facebook.Controllers
{
    [ApiController]
    [Route("api/esu/facebook/debug")]
   // [Authorize(Roles = "SuperAdmin")] // ⚠️ TEMPORARY for verification; lock down/remove after testing
    public sealed class FacebookEsuDebugController : ControllerBase
    {
        private readonly IFacebookTokenService _tokenService;
        private readonly AppDbContext _db;
        private readonly ILogger<FacebookEsuDebugController> _log;
        private readonly IFacebookGraphClient _graph;
        private readonly IEsuStatusService _status;
        public FacebookEsuDebugController(
            IFacebookTokenService tokenService,
            AppDbContext db,
            ILogger<FacebookEsuDebugController> log,
            IFacebookGraphClient graph,
            IEsuStatusService status)
        {
            _tokenService = tokenService;
            _db = db;
            _log = log;
            _graph = graph;
            _status = status;
        }

        // GET /api/esu/facebook/debug/token?businessId=...
        [HttpGet("token")]
        public async Task<IActionResult> GetToken([FromQuery] Guid businessId, CancellationToken ct)
        {
            if (businessId == Guid.Empty) return BadRequest("businessId is required.");

            var t = await _tokenService.TryGetValidAsync(businessId, ct);
            if (t is null)
            {
                return Ok(new
                {
                    ok = false,
                    message = "No valid token found (missing or expired). Re-run ESU."
                });
            }

            return Ok(new
            {
                ok = true,
                tokenPreview = Mask(t.AccessToken),
                expiresAtUtc = t.ExpiresAtUtc,
                willExpireSoon = t.WillExpireSoon(),  // default 5m skew
                rawJsonLength = t.RawJson?.Length ?? 0
            });
        }

        [HttpGet("status")]
        [Authorize]
        public async Task<ActionResult<EsuStatusDto>> GetStatus(CancellationToken ct)
        {
            // Uses the "businessId" claim from JWT (lowercase)
            var businessId = User.GetBusinessId();

            var dto = await _status.GetStatusAsync(businessId, ct);

            // Keep it simple; frontend already normalizes shape
            return Ok(dto);
        }


        [HttpPost("deauthorize")]
        public async Task<IActionResult> Deauthorize([FromQuery] Guid businessId, CancellationToken ct)
        {
            if (businessId == Guid.Empty) return BadRequest("businessId is required.");
            await _status.DeauthorizeAsync(businessId, ct);
            return Ok(new { ok = true });
        }


        // GET /api/esu/facebook/debug/flags?businessId=...
        [HttpGet("flags")]
        public async Task<IActionResult> ListFlags([FromQuery] Guid businessId, CancellationToken ct)
        {
            if (businessId == Guid.Empty) return BadRequest("businessId is required.");

            // Your IntegrationFlags model is the single-row, column-style model
            var row = await _db.IntegrationFlags
                .AsNoTracking()
                .SingleOrDefaultAsync(x => x.BusinessId == businessId, ct);

            if (row is null)
            {
                return Ok(new { ok = true, count = 0, items = Array.Empty<object>() });
            }

            // Box each element as object so the array can be typed object[]
            var items = new object[]
            {
                new { key = "FACEBOOK_ESU_COMPLETED",value = row.FacebookEsuCompleted ? "true" : "false" }

            };

            return Ok(new { ok = true, count = items.Length, items });
        }

        private static string Mask(string? s)
        {
            if (string.IsNullOrEmpty(s)) return "";
            if (s.Length <= 10) return new string('*', s.Length);
            return $"{s.Substring(0, 6)}…{s.Substring(s.Length - 4)}";
        }

        private static string? Preview(string? s, int max)
        {
            if (string.IsNullOrEmpty(s)) return null;
            return s.Length <= max ? s : s.Substring(0, max) + "…";
        }

        // GET /api/esu/facebook/debug/me?businessId=...
        [HttpGet("me")]
        public async Task<IActionResult> GetMe([FromQuery] Guid businessId, CancellationToken ct)
        {
            if (businessId == Guid.Empty) return BadRequest("businessId is required.");

            var me = await _graph.GetAsync<dynamic>(businessId, "me", new Dictionary<string, string?>
            {
                ["fields"] = "id,name"
            }, ct);

            return Ok(me);
        }
    }
}

using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Options;
using xbytechat.api.Features.ESU.Facebook.Options;

namespace xbytechat.api.Features.ESU.Facebook.Controllers
{
    [ApiController]
    [Route("api/esu/facebook/webhook")]
    public sealed class FacebookWebhookController : ControllerBase
    {
        private readonly string _verifyToken;

        public FacebookWebhookController(IOptions<FacebookOptions> opts)
        {
            _verifyToken = opts.Value.VerifyToken ?? string.Empty;
        }

        // GET verify: echo hub.challenge if token matches
        [HttpGet]
        public IActionResult Verify([FromQuery(Name = "hub.mode")] string? mode,
                                    [FromQuery(Name = "hub.verify_token")] string? token,
                                    [FromQuery(Name = "hub.challenge")] string? challenge)
        {
            if (string.Equals(mode, "subscribe", StringComparison.OrdinalIgnoreCase) &&
                !string.IsNullOrWhiteSpace(token) &&
                token == _verifyToken &&
                !string.IsNullOrWhiteSpace(challenge))
            {
                return Content(challenge!, "text/plain");
            }
            return Forbid();
        }

        // POST stub: logs or routes ESU-related events (optional for App Review)
        [HttpPost]
        public async Task<IActionResult> Receive()
        {
            using var reader = new StreamReader(Request.Body);
            var body = await reader.ReadToEndAsync();
            // TODO: route event to your logger/queue if needed
            return Ok(new { ok = true });
