using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading;
using System.Threading.Tasks;
using FluentAssertions;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Logging.Abstractions;
using Microsoft.Extensions.Options;
using xbytechat.api;
using xbytechat.api.AuthModule.Models;
using xbytechat.api.Features.CampaignTracking.Logging;
using xbytechat.api.Features.CampaignTracking.Models;
using Xunit;

namespace xbytechat.api.Tests
{
    public class CampaignLogSinkTests
    {
        private sealed class TestOptionsMonitor<T> : IOptionsMonitor<T>
        {
            private readonly T _current;
            public TestOptionsMonitor(T current) => _current = current;
            public T CurrentValue => _current;
            public T Get(string? name) => _current;
            public IDisposable OnChange(Action<T, string> listener) => NullDisposable.Instance;

            private sealed class NullDisposable : IDisposable
            {
                public static readonly NullDisposable Instance = new();
                public void Dispose() { }
            }
        }

        private static ServiceProvider BuildProvider(string dbName)
        {
            var services = new ServiceCollection();
            services.AddLogging();
            services.AddDbContext<AppDbContext>(opt => opt.UseInMemoryDatabase(dbName));
            return services.BuildServiceProvider();
        }

        private static CampaignLogSink CreateSink(ServiceProvider sp)
        {
            var opts = new BatchingOptions
            {
                CampaignLog = new BatchingOptions.CampaignLogOptions
                {
                    MaxBatchSize = 100,
                    UseCopy = false
                }
            };
            return new CampaignLogSink(
                NullLogger<CampaignLogSink>.Instance,
                sp,
                new TestOptionsMonitor<BatchingOptions>(opts));
        }

        [Fact]
        public async Task FlushAsync_WaitsForMissingMessageLogs_ThenInserts()
        {
            var dbName = Guid.NewGuid().ToString();
            using var sp = BuildProvider(dbName);
            var sink = CreateSink(sp);

            var msgId1 = Guid.NewGuid();
            var msgId2 = Guid.NewGuid(); // missing initially
            var businessId = Guid.NewGuid();
            var campaignId = Guid.NewGuid();
            var recipientId = Guid.NewGuid();

            // Seed only the first message log
            using (var scope = sp.CreateScope())
            {
                var db = scope.ServiceProvider.GetRequiredService<AppDbContext>();
                db.MessageLogs.Add(new MessageLog
                {
                    Id = msgId1,
                    BusinessId = businessId,
                    CampaignId = campaignId,
                    RecipientNumber = "123",
                    MessageContent = "hi",
                    Status = "Sent",
                    CreatedAt = DateTime.UtcNow
                });
                db.SaveChanges();
            }

            sink.Enqueue(NewRecord(businessId, campaignId, recipientId, msgId1));
            sink.Enqueue(NewRecord(businessId, campaignId, recipientId, msgId2)); // missing

            // First flush: should defer due to missing msgId2
            await sink.FlushAsync();
            using (var scope = sp.CreateScope())
            {
                var db = scope.ServiceProvider.GetRequiredService<AppDbContext>();
                db.CampaignSendLogs.Count().Should().Be(0);
            }

            // Add the missing message log
            using (var scope = sp.CreateScope())
            {
                var db = scope.ServiceProvider.GetRequiredService<AppDbContext>();
                db.MessageLogs.Add(new MessageLog
                {
                    Id = msgId2,
                    BusinessId = businessId,
                    CampaignId = campaignId,
                    RecipientNumber = "456",
                    MessageContent = "hi",
                    Status = "Sent",
                    CreatedAt = DateTime.UtcNow
                });
                db.SaveChanges();
            }

            // Second flush: should insert both send logs
            await sink.FlushAsync();
            using (var scope = sp.CreateScope())
            {
                var db = scope.ServiceProvider.GetRequiredService<AppDbContext>();
                db.CampaignSendLogs.Count().Should().Be(2);
                db.CampaignSendLogs.Select(x => x.MessageLogId).Should().BeEquivalentTo(new[] { msgId1, msgId2 });
            }
        }

        [Fact]
        public async Task FlushAsync_DropsBatchAfterMaxRetries_WhenMessageLogsMissing()
        {
            var dbName = Guid.NewGuid().ToString();
            using var sp = BuildProvider(dbName);
            var sink = CreateSink(sp);

            var missingId = Guid.NewGuid();
            var businessId = Guid.NewGuid();
            var campaignId = Guid.NewGuid();
            var recipientId = Guid.NewGuid();

            sink.Enqueue(NewRecord(businessId, campaignId, recipientId, missingId));

            // Exceed max attempts (3) by flushing 4 times
            for (int i = 0; i < 4; i++)
            {
                await sink.FlushAsync();
            }

            using var scope = sp.CreateScope();
            var db = scope.ServiceProvider.GetRequiredService<AppDbContext>();
            db.CampaignSendLogs.Count().Should().Be(0);
        }

        private static CampaignLogRecord NewRecord(Guid businessId, Guid campaignId, Guid recipientId, Guid? messageLogId)
        {
            var now = DateTime.UtcNow;
            return new CampaignLogRecord(
                Id: Guid.NewGuid(),
                RunId: Guid.NewGuid(),
                MessageId: "mid",
                CampaignId: campaignId,
                ContactId: null,
                RecipientId: recipientId,
                MessageBody: "body",
                TemplateId: "tpl",
                SendStatus: "Sent",
                ErrorMessage: null,
                CreatedAt: now,
                CreatedBy: "test",
                SentAt: now,
                DeliveredAt: null,
                ReadAt: null,
                IpAddress: null,
                DeviceInfo: null,
                MacAddress: null,
                SourceChannel: "campaign",
                DeviceType: null,
                Browser: null,
                Country: null,
                City: null,
                IsClicked: false,
                ClickedAt: null,
                ClickType: null,
                RetryCount: 0,
                LastRetryAt: null,
                LastRetryStatus: null,
                AllowRetry: true,
                MessageLogId: messageLogId,
                BusinessId: businessId,
                CTAFlowConfigId: null,
                CTAFlowStepId: null,
                ButtonBundleJson: null
            );
        }
    }
}

using System;
using System.Threading.Tasks;
using FluentAssertions;
using Microsoft.EntityFrameworkCore;
using Xunit;
using xbytechat.api.CRM.Dtos;
using xbytechat.api.CRM.Models;
using xbytechat.api.CRM.Services;


namespace xbytechat.api.Tests.Services
{
    public class ContactServiceTests
    {
        private readonly DbContextOptions<AppDbContext> _dbOptions;

        public ContactServiceTests()
        {
            _dbOptions = new DbContextOptionsBuilder<AppDbContext>()
                .UseInMemoryDatabase(databaseName: $"ContactTestDb_{Guid.NewGuid()}")
                .Options;
        }

        [Fact]
        public async Task AddContactAsync_ShouldAddNewContact()
        {
            // Arrange
            var businessId = Guid.NewGuid();
            var dto = new ContactDto
            {
                Name = "John Tester",
                PhoneNumber = "9876543210",
                Email = "john@example.com",
                LeadSource = "WhatsApp",
                Tags = "lead,new"
            };

            using var context = new AppDbContext(_dbOptions);
            var service = new ContactService(context);

            // Act
            var result = await service.AddContactAsync(businessId, dto);

            // Assert
            result.Should().NotBeNull();
            result.Name.Should().Be("John Tester");
            result.PhoneNumber.Should().Be("9876543210");

            var saved = await context.Contacts.FirstOrDefaultAsync();
            saved.Should().NotBeNull();
            saved.BusinessId.Should().Be(businessId);
            saved.Email.Should().Be("john@example.com");
        }

        [Fact]
        public async Task GetAllContactsAsync_ShouldReturnAllContactsForBusiness()
        {
            // Arrange
            var businessId1 = Guid.NewGuid();
            var businessId2 = Guid.NewGuid();

            var contact1 = new Contact
            {
                Id = Guid.NewGuid(),
                BusinessId = businessId1,
                Name = "Alice",
                PhoneNumber = "1111111111",
                CreatedAt = DateTime.UtcNow
            };

            var contact2 = new Contact
            {
                Id = Guid.NewGuid(),
                BusinessId = businessId1,
                Name = "Bob",
                PhoneNumber = "2222222222",
                CreatedAt = DateTime.UtcNow
            };

            var contactOtherBusiness = new Contact
            {
                Id = Guid.NewGuid(),
                BusinessId = businessId2,
                Name = "Charlie",
                PhoneNumber = "3333333333",
                CreatedAt = DateTime.UtcNow
            };

            using (var context = new AppDbContext(_dbOptions))
            {
                await context.Contacts.AddRangeAsync(contact1, contact2, contactOtherBusiness);
                await context.SaveChangesAsync();
            }

            using (var context = new AppDbContext(_dbOptions))
            {
                var service = new ContactService(context);

                // Act
                var result = await service.GetAllContactsAsync(businessId1);

                // Assert
                result.Should().NotBeNull();
                result.Should().HaveCount(2);
                result.Should().OnlyContain(c => c.PhoneNumber == "1111111111" || c.PhoneNumber == "2222222222");
            }
        }

        [Fact]
        public async Task UpdateContactAsync_ShouldUpdateFields()
        {
            // Arrange
            var businessId = Guid.NewGuid();
            var originalContact = new Contact
            {
                Id = Guid.NewGuid(),
                BusinessId = businessId,
                Name = "Old Name",
                PhoneNumber = "9999999999",
                Email = "old@example.com",
                CreatedAt = DateTime.UtcNow
            };

            using (var context = new AppDbContext(_dbOptions))
            {
                // Seed the original contact into the database
                await context.Contacts.AddAsync(originalContact);
                await context.SaveChangesAsync();
            }

            // Create a DTO with updated data to simulate what frontend sends
            var updatedDto = new ContactDto
            {
                Id = originalContact.Id,
                Name = "New Name",
                PhoneNumber = "1234567890",
                Email = "new@example.com",
                LeadSource = "Google",
                Tags = "priority,updated",
                LastContactedAt = DateTime.UtcNow,
                Notes = "This is a test update"
            };

            using (var context = new AppDbContext(_dbOptions))
            {
                var service = new ContactService(context);

                // Act: Call the update method
                var success = await service.UpdateContactAsync(businessId, updatedDto);

                // Assert 1: Ensure the method returns true
                success.Should().BeTrue();

                // Fetch the updated contact


            }
        }


        // Step 1: Seed the DB with one contact to delete
        // This simulates a real record in the system
        // Step 2: Call the DeleteContactAsync method from the service
        // This should remove the record with the matching ID + BusinessId
        // Step 3: Assert deletion was successful (true returned)
        // Then re-query the DB to ensure the contact no longer exists

        [Fact]
        public async Task DeleteContactAsync_ShouldRemoveRecord()
        {
            // Arrange
            var businessId = Guid.NewGuid();
            var contactToDelete = new Contact
            {
                Id = Guid.NewGuid(),
                BusinessId = businessId,
                Name = "Delete Me",
                PhoneNumber = "7777777777",
                CreatedAt = DateTime.UtcNow
            };

            using (var context = new AppDbContext(_dbOptions))
            {
                // Add the contact to the in-memory DB
                await context.Contacts.AddAsync(contactToDelete);
                await context.SaveChangesAsync();
            }

            using (var context = new AppDbContext(_dbOptions))
            {
                var service = new ContactService(context);

                // Act: Attempt to delete the contact
                var result = await service.DeleteContactAsync(businessId, contactToDelete.Id);

                // Assert 1: Deletion should return true
                result.Should().BeTrue();

                // Assert 2: Contact should not exist in the database anymore
                var deleted = await context.Contacts.FindAsync(contactToDelete.Id);
                deleted.Should().BeNull();
            }
        }

    }
}

