using System.Text.RegularExpressions;
using xbytechat_api.WhatsAppSettings.Services; // IWhatsAppSettingsService
using xbytechat.api.Features.TemplateModule.Abstractions;

namespace xbytechat.api.Features.TemplateModule.Services;

public sealed class MetaCredentialsResolver : IMetaCredentialsResolver
{
    private readonly IWhatsAppSettingsService _wa;

    // Matches .../vXX or .../vXX.X (optionally with trailing slash)
    private static readonly Regex VersionSeg = new(@"/(v\d+(?:\.\d+)?)\/?$", RegexOptions.IgnoreCase | RegexOptions.Compiled);

    public MetaCredentialsResolver(IWhatsAppSettingsService waSettings)
    {
        _wa = waSettings;
    }

    public async Task<MetaCredentials> ResolveAsync(Guid businessId, CancellationToken ct = default)
    {
        // Your existing call; if you have a ct-aware version, wire it here.
        var s = await _wa.GetSettingsByBusinessIdAsync(businessId);
        if (s is null) throw new InvalidOperationException("WhatsApp settings not found for this business.");

        var apiUrl = (s.ApiUrl ?? string.Empty).Trim().TrimEnd('/');
        var token = (s.ApiKey ?? string.Empty).Trim();
        var wabaId = (s.WabaId ?? string.Empty).Trim();

        if (string.IsNullOrWhiteSpace(apiUrl)) throw new InvalidOperationException("Meta ApiUrl is missing.");
        if (string.IsNullOrWhiteSpace(token)) throw new InvalidOperationException("Meta access token is missing.");
        if (string.IsNullOrWhiteSpace(wabaId)) throw new InvalidOperationException("Meta WABA ID is missing.");

        // If ApiUrl already includes a version (e.g., https://graph.facebook.com/v21.0)
        // split it into base + version; otherwise leave version empty.
        string baseUrl = apiUrl;
        string version = string.Empty;

        var m = VersionSeg.Match(apiUrl);
        if (m.Success)
        {
            version = m.Groups[1].Value;                                // e.g., v21.0
            baseUrl = apiUrl.Substring(0, m.Index).TrimEnd('/');        // e.g., https://graph.facebook.com
        }

        return new MetaCredentials(
            AccessToken: token,
            GraphBaseUrl: baseUrl,
            GraphVersion: version,   // can be "" if ApiUrl had no version
            WabaId: wabaId
        );
    }
}

using System.Net;
using System.Net.Http.Headers;
using System.Text;
using System.Text.Json;
using Microsoft.Extensions.Logging;
using xbytechat.api.Features.TemplateModule.Abstractions;

namespace xbytechat.api.Features.TemplateModule.Services;

public sealed class MetaTemplateClient : IMetaTemplateClient
{
    private readonly IHttpClientFactory _httpClientFactory;
    private readonly IMetaCredentialsResolver _creds;
    private readonly ILogger<MetaTemplateClient> _log;

    private static readonly JsonSerializerOptions JsonOpts = new(JsonSerializerDefaults.Web)
    {
        WriteIndented = false
    };

    public MetaTemplateClient(
        IHttpClientFactory httpClientFactory,
        IMetaCredentialsResolver creds,
        ILogger<MetaTemplateClient> log)
    {
        _httpClientFactory = httpClientFactory;
        _creds = creds;
        _log = log;
    }

    public async Task<string> UploadMediaAsync(Guid businessId, string localPathOrUrl, string mediaType, CancellationToken ct = default)
    {
        // For template headers, Meta requires an "asset handle" (aka header_handle).
        // If the caller passes an already-generated handle, accept it.
        if (localPathOrUrl.StartsWith("handle:", StringComparison.OrdinalIgnoreCase))
            return localPathOrUrl.Substring("handle:".Length);

        // Otherwise, we expect the caller to have performed the Resumable Upload flow.
        throw new NotSupportedException(
            "Media headers require a Meta asset handle (header_handle). " +
            "Upload the media via Meta Resumable Upload API first and pass the resulting handle " +
            "(prefix with 'handle:' if you want to bypass this guard).");
    }

    public async Task<bool> CreateTemplateAsync(
        Guid businessId,
        string name,
        string category,
        string language,
        object componentsPayload,
        object examplesPayload,
        CancellationToken ct = default)
    {
        // Resolve real credentials from your WhatsAppSettings-backed resolver.
        // Your resolver can return either:
        //  - GraphBaseUrl + GraphVersion (e.g., https://graph.facebook.com + v21.0), or
        //  - ApiUrl that already includes a version (e.g., https://graph.facebook.com/v21.0)
        var c = await _creds.ResolveAsync(businessId, ct);

        // Build base URL robustly whether version is present or not.
        // If your resolver sets GraphVersion to "", pathPart becomes "".
        var baseRoot = (c.GraphBaseUrl ?? "").TrimEnd('/');
        var versionPart = string.IsNullOrWhiteSpace(c.GraphVersion) ? "" : "/" + c.GraphVersion.Trim('/');
        var baseWithVersion = $"{baseRoot}{versionPart}/";

        var client = _httpClientFactory.CreateClient("meta-graph");
        client.BaseAddress = new Uri(baseWithVersion);
        client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", c.AccessToken);

        // Per Meta docs: POST /{WABA_ID}/message_templates
        // Body: name, category, language, components (examples for variables embedded via BODY).
        var payload = new
        {
            name,
            category,
            allow_category_change = true, // helps reduce rejections due to miscategorization
            language,
            components = componentsPayload
            // NOTE: If you later need top-level examples, include them here per the latest API version.
        };

        using var content = new StringContent(JsonSerializer.Serialize(payload, JsonOpts), Encoding.UTF8, "application/json");
        using var resp = await client.PostAsync($"{c.WabaId}/message_templates", content, ct);

        var body = await resp.Content.ReadAsStringAsync(ct);

        if (resp.IsSuccessStatusCode)
        {
            _log.LogInformation("Meta create template OK | WABA {WabaId} | name {Name} | lang {Lang}", c.WabaId, name, language);
            return true;
        }

        // Try to parse Meta error for clearer diagnostics.
        string friendlyError = ExtractMetaError(body, (int)resp.StatusCode);

        // Common statuses:
        // 400: invalid payload (components/examples/name)
        // 401/403: auth/permission
        // 409: duplicate name+language
        // 429/5xx: rate limiting / transient errors
        _log.LogWarning("Meta create failed ({StatusCode} {Status}): {Error}",
            (int)resp.StatusCode, resp.StatusCode, friendlyError);

        return false;
    }

    public Task<int> SyncTemplatesAsync(Guid businessId, CancellationToken ct = default)
        => Task.FromResult(0);



    // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

    private static string ExtractMetaError(string responseBody, int statusCode)
    {
        try
        {
            // Meta error shape: { "error": { "message": "...", "type": "...", "code": 0, "error_subcode": 0, "fbtrace_id": "..." } }
            using var doc = JsonDocument.Parse(responseBody);
            var root = doc.RootElement;

            if (root.TryGetProperty("error", out var err))
            {
                var msg = err.TryGetProperty("message", out var m) ? m.GetString() : null;
                var type = err.TryGetProperty("type", out var t) ? t.GetString() : null;
                var code = err.TryGetProperty("code", out var c) ? c.GetInt32() : (int?)null;
                var sub = err.TryGetProperty("error_subcode", out var s) ? s.GetInt32() : (int?)null;
                var fb = err.TryGetProperty("fbtrace_id", out var f) ? f.GetString() : null;

                return $"Meta error: {(type ?? "N/A")} | code {(code?.ToString() ?? "N/A")}" +
                       $"{(sub.HasValue ? $" subcode {sub}" : "")} | message: {msg ?? "N/A"} | fbtrace_id: {fb ?? "N/A"}";
            }
        }
        catch
        {
            // ignore JSON parse errors, fall back to raw body
        }

        // Fallback to raw text if not JSON or unexpected
        return $"HTTP {statusCode}: {responseBody}";
    }

    public async Task<bool> DeleteTemplateAsync(Guid businessId, string name, string language, CancellationToken ct = default)
    {
        var c = await _creds.ResolveAsync(businessId, ct);
        var client = _httpClientFactory.CreateClient("meta-graph");
        client.BaseAddress = new Uri($"{c.GraphBaseUrl.TrimEnd('/')}/{c.GraphVersion.Trim('/')}/");
        client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", c.AccessToken);

        // Graph supports DELETE on /{wabaId}/message_templates with query params name & language
        var url = $"{c.WabaId}/message_templates?name={Uri.EscapeDataString(name)}&language={Uri.EscapeDataString(language)}";
        using var resp = await client.DeleteAsync(url, ct);
        var body = await resp.Content.ReadAsStringAsync(ct);

        if (resp.IsSuccessStatusCode)
        {
            _log.LogInformation("Meta delete template OK | WABA {WabaId} | name {Name} | lang {Lang}", c.WabaId, name, language);
            return true;
        }

        // 404/400 means already gone or not found; treat as success from UX standpoint
        if ((int)resp.StatusCode == 404 || (int)resp.StatusCode == 400)
        {
            _log.LogWarning("Meta delete returned {Code} but treating as success. Body: {Body}", resp.StatusCode, body);
            return true;
        }

        _log.LogWarning("Meta delete failed ({Code}): {Body}", resp.StatusCode, body);
        return false;
    }

}

using System;
using System.IO;
using System.Linq;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Collections.Generic;
using System.Text.Json;
using System.Threading;
using System.Threading.Tasks;
using Microsoft.Extensions.Options;
using xbytechat.api.Features.TemplateModule.Abstractions;
using xbytechat.api.Features.TemplateModule.Config;

namespace xbytechat.api.Features.TemplateModule.Services;

public sealed class MetaUploadService : IMetaUploadService
{
    private readonly IHttpClientFactory _httpFactory;
    private readonly IMetaCredentialsResolver _creds;
    private readonly UploadLimitsOptions _opts;

    public MetaUploadService(
        IHttpClientFactory httpFactory,
        IMetaCredentialsResolver creds,
        IOptions<UploadLimitsOptions> opts)
    {
        _httpFactory = httpFactory;
        _creds = creds;
        _opts = opts.Value;
    }

    public async Task<HeaderUploadResult> UploadHeaderAsync(
        Guid businessId,
        HeaderMediaType mediaType,
        Stream? fileStream,
        string? fileName,
        string? sourceUrl,
        CancellationToken ct = default)
    {
        if ((fileStream is null && string.IsNullOrWhiteSpace(sourceUrl)) ||
            (fileStream is not null && !string.IsNullOrWhiteSpace(sourceUrl)))
            throw new ArgumentException("Provide either a file or a sourceUrl, not both.");

        // If sourceUrl provided, download then proceed
        if (fileStream is null && sourceUrl is not null)
        {
            using var http = new HttpClient();
            using var resp = await http.GetAsync(sourceUrl, HttpCompletionOption.ResponseHeadersRead, ct);
            resp.EnsureSuccessStatusCode();

            var tmp = new MemoryStream();
            await resp.Content.CopyToAsync(tmp, ct);
            tmp.Position = 0;

            var mime = resp.Content.Headers.ContentType?.MediaType ?? "application/octet-stream";
            var fname = Path.GetFileName(new Uri(sourceUrl).LocalPath);
            return await UploadCoreAsync(businessId, mediaType, tmp, fname, mime, ct);
        }

        // File provided
        if (fileStream is not null)
        {
            var mime = GuessMime(fileName, mediaType);
            return await UploadCoreAsync(businessId, mediaType, fileStream, fileName ?? "upload.bin", mime, ct);
        }

        throw new InvalidOperationException("No file or URL provided.");
    }

    private async Task<HeaderUploadResult> UploadCoreAsync(
        Guid businessId,
        HeaderMediaType mediaType,
        Stream content,
        string fileName,
        string mime,
        CancellationToken ct)
    {
        // Ensure we can read length + seek (so we can retry Path B after Path A)
        if (!content.CanSeek)
        {
            var mem = new MemoryStream();
            await content.CopyToAsync(mem, ct);
            mem.Position = 0;
            content = mem;
        }

        var size = content.Length;

        // Size/MIME guards
        switch (mediaType)
        {
            case HeaderMediaType.IMAGE:
                if (size > _opts.ImageMaxBytes) throw new InvalidOperationException("Image too large.");
                if (!_opts.AllowedImageMime.Contains(mime, StringComparer.OrdinalIgnoreCase))
                    throw new InvalidOperationException($"Invalid image MIME: {mime}");
                break;

            case HeaderMediaType.VIDEO:
                if (size > _opts.VideoMaxBytes) throw new InvalidOperationException("Video too large.");
                if (!_opts.AllowedVideoMime.Contains(mime, StringComparer.OrdinalIgnoreCase))
                    throw new InvalidOperationException($"Invalid video MIME: {mime}");
                break;

            case HeaderMediaType.DOCUMENT:
                if (size > _opts.DocumentMaxBytes) throw new InvalidOperationException("Document too large.");
                if (!_opts.AllowedDocMime.Contains(mime, StringComparer.OrdinalIgnoreCase))
                    throw new InvalidOperationException($"Invalid document MIME: {mime}");
                break;
        }

        // ‚îÄ‚îÄ STUB MODE ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
        if (_opts.UseStubHandle)
        {
            var handleStub = $"{(int)mediaType}:{Guid.NewGuid():N}".Insert(1, "::");
            return new HeaderUploadResult(handleStub, mime, size, IsStub: true);
        }

        // ‚îÄ‚îÄ REAL MODE: Meta Resumable Upload ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
        var c = await _creds.ResolveAsync(businessId, ct);

        var baseRoot = (c.GraphBaseUrl ?? "").TrimEnd('/');
        var versionPart = string.IsNullOrWhiteSpace(c.GraphVersion) ? "" : "/" + c.GraphVersion.Trim('/');
        var baseWithVersion = $"{baseRoot}{versionPart}";

        using var client = _httpFactory.CreateClient("meta-graph");
        client.BaseAddress = new Uri(baseWithVersion + "/");
        client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", c.AccessToken);

        // Meta expects IMAGE|VIDEO|DOCUMENT
        string fileTypeParam = mediaType switch
        {
            HeaderMediaType.IMAGE => "IMAGE",
            HeaderMediaType.VIDEO => "VIDEO",
            HeaderMediaType.DOCUMENT => "DOCUMENT",
            _ => "DOCUMENT"
        };

        // Path A: uploads session
        var handle = await TryPathA_UploadsEndpointAsync(client, c.WabaId, fileTypeParam, content, fileName, mime, size, ct);
        if (!string.IsNullOrWhiteSpace(handle))
            return new HeaderUploadResult(handle!, mime, size, false);

        // Rewind before fallback (Path A already consumed the stream)
        try { content.Position = 0; } catch { /* ignore */ }

        // Path B: phased upload
        handle = await TryPathB_PhasedUploadAsync(client, c.WabaId, fileTypeParam, content, fileName, mime, size, ct);
        if (!string.IsNullOrWhiteSpace(handle))
            return new HeaderUploadResult(handle!, mime, size, false);

        throw new InvalidOperationException("Meta upload did not return an asset handle. Check app permissions and the response logs.");
    }

    // ‚îÄ‚îÄ Path A: WABA uploads session (single-shot Content-Range) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private async Task<string?> TryPathA_UploadsEndpointAsync(
        HttpClient client,
        string wabaId,
        string fileTypeParam,
        Stream content,
        string fileName,
        string mime,
        long size,
        CancellationToken ct)
    {
        // INIT
        var initPayload = new Dictionary<string, string>
        {
            ["file_type"] = fileTypeParam,
            ["file_length"] = size.ToString()
        };
        using var initResp = await client.PostAsync($"{wabaId}/uploads", new FormUrlEncodedContent(initPayload), ct);
        var initText = await initResp.Content.ReadAsStringAsync(ct);
        if (!initResp.IsSuccessStatusCode) return null;

        var initJson = SafeParse(initText);
        var uploadId = initJson.TryGetValue("id", out var idObj) ? idObj?.ToString() : null;
        if (string.IsNullOrWhiteSpace(uploadId)) return null;

        // TRANSFER (single range)
        using (var req = new HttpRequestMessage(HttpMethod.Post, uploadId))
        {
            req.Content = new StreamContent(content);
            req.Content.Headers.ContentType = new MediaTypeHeaderValue("application/octet-stream");
            req.Content.Headers.ContentLength = size;
            req.Headers.TryAddWithoutValidation("Content-Range", $"bytes 0-{size - 1}/{size}");

            using var upResp = await client.SendAsync(req, HttpCompletionOption.ResponseHeadersRead, ct);
            var upText = await upResp.Content.ReadAsStringAsync(ct);
            if (!upResp.IsSuccessStatusCode) return null;
        }

        // FINISH
        var finPayload = new Dictionary<string, string> { ["finish"] = "true" };
        using var finResp = await client.PostAsync(uploadId, new FormUrlEncodedContent(finPayload), ct);
        var finText = await finResp.Content.ReadAsStringAsync(ct);
        if (!finResp.IsSuccessStatusCode) return null;

        var finJson = SafeParse(finText);
        if (TryExtractHandle(finJson, out var handle)) return handle;

        return null;
    }

    // ‚îÄ‚îÄ Path B: classic phased upload (start/transfer/finish) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private async Task<string?> TryPathB_PhasedUploadAsync(
        HttpClient client,
        string wabaId,
        string fileTypeParam,
        Stream content,
        string fileName,
        string mime,
        long size,
        CancellationToken ct)
    {
        // START
        var startPayload = new Dictionary<string, string>
        {
            ["upload_phase"] = "start",
            ["file_type"] = fileTypeParam,
            ["file_length"] = size.ToString()
        };
        using var startResp = await client.PostAsync($"{wabaId}/uploads", new FormUrlEncodedContent(startPayload), ct);
        var startText = await startResp.Content.ReadAsStringAsync(ct);
        if (!startResp.IsSuccessStatusCode) return null;

        var startJson = SafeParse(startText);
        var sessionId = startJson.TryGetValue("id", out var idObj) ? idObj?.ToString() : null;
        if (string.IsNullOrWhiteSpace(sessionId)) return null;

        // TRANSFER
        using (var req = new HttpRequestMessage(HttpMethod.Post, $"{sessionId}?upload_phase=transfer"))
        {
            req.Content = new StreamContent(content);
            req.Content.Headers.ContentType = new MediaTypeHeaderValue("application/octet-stream");
            req.Content.Headers.ContentLength = size;
            req.Headers.TryAddWithoutValidation("Content-Range", $"bytes 0-{size - 1}/{size}");

            using var transferResp = await client.SendAsync(req, HttpCompletionOption.ResponseHeadersRead, ct);
            var transferText = await transferResp.Content.ReadAsStringAsync(ct);
            if (!transferResp.IsSuccessStatusCode) return null;
        }

        // FINISH
        using (var req = new HttpRequestMessage(HttpMethod.Post, $"{sessionId}?upload_phase=finish"))
        {
            req.Content = new FormUrlEncodedContent(new Dictionary<string, string> { ["confirm"] = "true" });

            using var finishResp = await client.SendAsync(req, ct);
            var finishText = await finishResp.Content.ReadAsStringAsync(ct);
            if (!finishResp.IsSuccessStatusCode) return null;

            var finJson = SafeParse(finishText);
            if (TryExtractHandle(finJson, out var handle)) return handle;
        }

        return null;
    }

    // ‚îÄ‚îÄ JSON helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private static Dictionary<string, object?> SafeParse(string json)
    {
        try
        {
            var root = JsonSerializer.Deserialize<Dictionary<string, object?>>(json);
            return root ?? new Dictionary<string, object?>();
        }
        catch
        {
            return new Dictionary<string, object?>();
        }
    }

    private static bool TryExtractHandle(Dictionary<string, object?> json, out string? handle)
    {
        handle = null;

        if (json.TryGetValue("h", out var h) && !string.IsNullOrWhiteSpace(h?.ToString()))
        {
            handle = h!.ToString();
            return true;
        }

        if (json.TryGetValue("handle", out var hh) && !string.IsNullOrWhiteSpace(hh?.ToString()))
        {
            handle = hh!.ToString();
            return true;
        }

        if (json.TryGetValue("asset_handle", out var ah) && !string.IsNullOrWhiteSpace(ah?.ToString()))
        {
            handle = ah!.ToString();
            return true;
        }

        // sometimes nested { "result": { "h": "4::..." } }
        if (json.TryGetValue("result", out var resObj) &&
            resObj is JsonElement el &&
            el.ValueKind == JsonValueKind.Object)
        {
            if (el.TryGetProperty("h", out var h2) && h2.ValueKind == JsonValueKind.String)
            {
                handle = h2.GetString();
                return true;
            }
            if (el.TryGetProperty("handle", out var h3) && h3.ValueKind == JsonValueKind.String)
            {
                handle = h3.GetString();
                return true;
            }
            if (el.TryGetProperty("asset_handle", out var h4) && h4.ValueKind == JsonValueKind.String)
            {
                handle = h4.GetString();
                return true;
            }
        }

        return false;
    }

    // ‚îÄ‚îÄ MIME guessing ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private static string GuessMime(string? fileName, HeaderMediaType mediaType)
    {
        var ext = Path.GetExtension(fileName ?? string.Empty).ToLowerInvariant();

        if (mediaType == HeaderMediaType.IMAGE)
            return ext switch
            {
                ".jpg" or ".jpeg" => "image/jpeg",
                ".png" => "image/png",
                ".webp" => "image/webp",
                _ => "image/jpeg"
            };

        if (mediaType == HeaderMediaType.VIDEO)
            return "video/mp4";

        if (mediaType == HeaderMediaType.DOCUMENT)
            return ext switch
            {
                ".pdf" => "application/pdf",
                _ => "application/pdf"
            };

        return "application/octet-stream";
    }
}

using Microsoft.EntityFrameworkCore;
using xbytechat.api.Features.TemplateModule.Abstractions;
using xbytechat.api.Features.TemplateModule.Models;
using xbytechat.api.Features.TemplateModule.Services;
using xbytechat.api.Features.WhatsAppSettings.Models;

namespace xbytechat.api.Features.TemplateModule.Services;

public sealed class TemplateDraftLifecycleService : ITemplateDraftLifecycleService
{
    private readonly AppDbContext _db;
    private readonly IMetaTemplateClient _meta;

    public TemplateDraftLifecycleService(AppDbContext db, IMetaTemplateClient meta)
    {
        _db = db;
        _meta = meta;
    }

    public async Task<TemplateDraft> DuplicateDraftAsync(Guid businessId, Guid draftId, CancellationToken ct = default)
    {
        var src = await _db.TemplateDrafts.AsNoTracking()
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);
        if (src is null) throw new KeyNotFoundException("Draft not found.");

        var keyBase = src.Key;
        var suffix = 2;
        var newKey = $"{keyBase}_copy";
        while (await _db.TemplateDrafts.AnyAsync(x => x.BusinessId == businessId && x.Key == newKey, ct))
        {
            newKey = $"{keyBase}_copy{suffix}";
            suffix++;
        }

        var now = DateTime.UtcNow;
        var dup = new TemplateDraft
        {
            Id = Guid.NewGuid(),
            BusinessId = businessId,
            Key = newKey,
            Category = src.Category,
            DefaultLanguage = src.DefaultLanguage,
            CreatedAt = now,
            UpdatedAt = now
        };

        _db.TemplateDrafts.Add(dup);

        var variants = await _db.TemplateDraftVariants
            .AsNoTracking()
            .Where(v => v.TemplateDraftId == src.Id)
            .ToListAsync(ct);

        foreach (var v in variants)
        {
            _db.TemplateDraftVariants.Add(new TemplateDraftVariant
            {
                Id = Guid.NewGuid(),
                TemplateDraftId = dup.Id,
                Language = v.Language,
                HeaderType = v.HeaderType,
                HeaderText = v.HeaderText,
                HeaderMediaLocalUrl = v.HeaderMediaLocalUrl, // keep handle if present; user can replace
                BodyText = v.BodyText,
                FooterText = v.FooterText,
                ButtonsJson = v.ButtonsJson,
                ExampleParamsJson = v.ExampleParamsJson,
                IsReadyForSubmission = v.IsReadyForSubmission,
                ValidationErrorsJson = v.ValidationErrorsJson
            });
        }

        await _db.SaveChangesAsync(ct);
        return dup;
    }


    public async Task<bool> DeleteDraftAsync(Guid businessId, Guid draftId, CancellationToken ct = default)
    {
        // 1) Find the draft scoped to the tenant
        var draft = await _db.TemplateDrafts
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);

        if (draft is null) return false;

        // 2) Hard-delete variants explicitly (no nav needed)

        // If you're on EF Core 7+, this is the fastest way:
        try
        {
            await _db.TemplateDraftVariants
                .Where(v => v.TemplateDraftId == draft.Id)
                .ExecuteDeleteAsync(ct);
        }
        catch (NotSupportedException)
        {
            // Fallback for EF Core < 7: load + RemoveRange
            var children = await _db.TemplateDraftVariants
                .Where(v => v.TemplateDraftId == draft.Id)
                .ToListAsync(ct);
            if (children.Count > 0)
                _db.TemplateDraftVariants.RemoveRange(children);
        }

        // 3) Delete the parent draft
        _db.TemplateDrafts.Remove(draft);

        // 4) Commit
        await _db.SaveChangesAsync(ct);
        return true;
    }


    public async Task<bool> DeleteApprovedTemplateAsync(Guid businessId, string name, string language, CancellationToken ct = default)
    {
        if (string.IsNullOrWhiteSpace(name) || string.IsNullOrWhiteSpace(language))
            throw new ArgumentException("Name and language are required.");

        // 1) Ask Meta to delete
        var okMeta = await _meta.DeleteTemplateAsync(businessId, name, language, ct);

        // 2) Soft-delete locally even if Meta already deleted (idempotent UX)
        var rows = await _db.WhatsAppTemplates
            .Where(t => t.BusinessId == businessId && t.Name == name && t.LanguageCode == language)
            .ToListAsync(ct);

        if (rows.Count > 0)
        {
            var now = DateTime.UtcNow;
            foreach (var t in rows)
            {
                t.IsActive = false;
                t.Status = "DELETED"; // keep a conventional marker
                t.UpdatedAt = now;
                t.LastSyncedAt = now;
            }
            await _db.SaveChangesAsync(ct);
        }

        return okMeta;
    }
}

using System.Text.Json;
using Microsoft.EntityFrameworkCore;
using xbytechat.api.Features.TemplateModule.Abstractions;
using xbytechat.api.Features.TemplateModule.DTOs;
using xbytechat.api.Features.TemplateModule.Models;
