using Microsoft.Extensions.Configuration;
using Newtonsoft.Json;
using xbytechat.api.WhatsAppSettings.DTOs;

namespace xbytechat.api.Features.TemplateModule.Services
{
    public class WhatsAppTemplateService : IWhatsAppTemplateService
    {
        private readonly HttpClient _httpClient;
        private readonly IConfiguration _config;
        private readonly ILogger<WhatsAppTemplateService> _logger;

        public WhatsAppTemplateService(HttpClient httpClient, IConfiguration config, ILogger<WhatsAppTemplateService> logger)
        {
            _httpClient = httpClient;
            _config = config;
            _logger = logger;
        }

        public async Task<List<TemplateMetadataDto>> FetchTemplatesAsync()
        {
            var wabaId = _config["WhatsApp:WABA_ID"];
            var token = _config["WhatsApp:apiToken"];
            var url = $"https://graph.facebook.com/v18.0/{wabaId}/message_templates";

            var templates = new List<TemplateMetadataDto>();

            try
            {
                _httpClient.DefaultRequestHeaders.Authorization = new System.Net.Http.Headers.AuthenticationHeaderValue("Bearer", token);
                var response = await _httpClient.GetAsync(url);
                var json = await response.Content.ReadAsStringAsync();

                if (!response.IsSuccessStatusCode)
                {
                    _logger.LogError("Failed to fetch WhatsApp templates: " + json);
                    return templates;
                }

                var parsed = JsonConvert.DeserializeObject<dynamic>(json);

                foreach (var tpl in parsed.data)
                {
                    string name = tpl.name;
                    string language = tpl.language ?? "en_US";
                    string body = "";

                    foreach (var component in tpl.components)
                    {
                        if (component.type == "BODY")
                        {
                            body = component.text;
                            break;
                        }
                    }

                    // Count {{placeholders}}
                    var placeholderCount = System.Text.RegularExpressions.Regex.Matches(body, "{{(.*?)}}").Count;

                    templates.Add(new TemplateMetadataDto
                    {
                        Name = name,
                        Language = language,
                        Body = body,
                        PlaceholderCount = placeholderCount
                    });
                }

                return templates;
            }
            catch (Exception ex)
            {
                _logger.LogError("Error while fetching templates from Meta: " + ex.Message);
                return templates;
            }
        }
    }
}

using System.Text;
using System.Text.RegularExpressions;

namespace xbytechat.api.Features.TemplateModule.Utils;

public static class MetaNameHelper
{
    // WhatsApp template name rules: lowercase, underscores, alnum and _ only, <= 25 chars.
    private static readonly Regex Allowed = new(@"[^a-z0-9_]+", RegexOptions.Compiled);

    public static string FromKey(string key, Guid businessId, string? suffix = null)
    {
        // 1) lower + underscores
        var s = key.Trim().ToLowerInvariant()
            .Replace(' ', '_')
            .Replace('-', '_');

        // 2) strip invalids
        s = Allowed.Replace(s, "");

        // 3) ensure starts with a letter
        if (s.Length == 0 || !char.IsLetter(s[0]))
            s = "t_" + s;

        // 4) add optional suffix for uniqueness (e.g., short biz hash)
        if (!string.IsNullOrWhiteSpace(suffix))
            s = $"{s}_{suffix}".TrimEnd('_');

        // 5) cap length 25 (WA limit)
        if (s.Length > 25) s = s[..25];

        // fallback safety
        if (string.IsNullOrWhiteSpace(s)) s = "t_" + businessId.ToString("N")[..6];
        return s;
    }

    public static string ShortBizSuffix(Guid businessId)
        => businessId.ToString("N")[..6];
}

using System.Text.Json;
using xbytechat.api.Features.TemplateModule.Validation;
using xbytechat.api.Features.TemplateModule.DTOs;

namespace xbytechat.api.Features.TemplateModule.Validators;

public static class MultiLanguageValidator
{
    public sealed class VariantView
    {
        public required string Language { get; init; }
        public required string BodyText { get; init; }
        public required string HeaderType { get; init; }
        public string? HeaderText { get; init; }
        public string? HeaderMediaLocalUrl { get; init; }
        public string? FooterText { get; init; }
        public required List<ButtonDto> Buttons { get; init; }
        public required Dictionary<string, string> Examples { get; init; }
    }

    public static (bool ok, Dictionary<string, List<string>> errors) Validate(IReadOnlyList<VariantView> variants)
    {
        var errors = new Dictionary<string, List<string>>(StringComparer.OrdinalIgnoreCase);
        bool OkLang(string lang) => !errors.ContainsKey(lang) || errors[lang].Count == 0;
        void Add(string lang, string msg)
        {
            if (!errors.TryGetValue(lang, out var list)) { list = new(); errors[lang] = list; }
            list.Add(msg);
        }

        // 1) Per-variant rules (reuse VariantValidator)
        foreach (var v in variants)
        {
            var dto = new TemplateDraftVariantUpsertDto
            {
                Language = v.Language,
                BodyText = v.BodyText,
                HeaderType = v.HeaderType,
                HeaderText = v.HeaderText,
                HeaderMediaLocalUrl = v.HeaderMediaLocalUrl,
                FooterText = v.FooterText,
                Buttons = v.Buttons,
                Examples = v.Examples
            };
            var r = VariantValidator.Validate(dto);
            foreach (var e in r.Errors) Add(v.Language, e);
        }

        // 2) Cross-language placeholder arity/order parity
        if (variants.Count > 1)
        {
            var reference = variants[0];
            var refSlots = PlaceholderHelper.ExtractSlots(reference.BodyText);
            var refSet = refSlots.ToHashSet();

            foreach (var v in variants.Skip(1))
            {
                var slots = PlaceholderHelper.ExtractSlots(v.BodyText);
                var set = slots.ToHashSet();

                // arity parity
                if (set.Count != refSet.Count)
                    Add(v.Language, $"Placeholder count mismatch vs {reference.Language}: expected {refSet.Count}, got {set.Count}.");

                // exact set parity (e.g., {1,2,3})
                if (!set.SetEquals(refSet))
                    Add(v.Language, $"Placeholder indices mismatch vs {reference.Language}: expected {{{string.Join(",", refSet.OrderBy(x => x))}}}.");

                // continuity check already handled per-variant; this ensures cross-lang equality.
            }
        }

        var ok = errors.Values.All(l => l.Count == 0);
        return (ok, errors);
    }
}

using System.Text.RegularExpressions;

namespace xbytechat.api.Features.TemplateModule.Validation;

public static class PlaceholderHelper
{
    private static readonly Regex PlaceholderRx = new(@"\{\{(\d+)\}\}", RegexOptions.Compiled);

    public static List<int> ExtractSlots(string? text)
    {
        var list = new List<int>();
        if (string.IsNullOrEmpty(text)) return list;
        foreach (Match m in PlaceholderRx.Matches(text))
        {
            if (int.TryParse(m.Groups[1].Value, out var n)) list.Add(n);
        }
        return list;
    }

    public static (bool ok, string? error) EnsureContinuousFrom1(IReadOnlyCollection<int> slots)
    {
        if (slots.Count == 0) return (true, null);
        var max = slots.Max();
        var set = slots.ToHashSet();
        for (int i = 1; i <= max; i++)
        {
            if (!set.Contains(i)) return (false, $"Missing placeholder {{${i}}} (placeholders must be continuous from {{1}}..{{{max}}}).".Replace("${i}", i.ToString()));
        }
        return (true, null);
    }
}

