using System;
using System.IO;
using System.Linq;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using ClosedXML.Excel;
using xbytechat.api.Features.Tracking.Services;

namespace xbytechat.api.Features.Tracking.Services
{
    public sealed class JourneyExportService : IJourneyExportService
    {
        private readonly IContactJourneyService _journeyService;

        public JourneyExportService(IContactJourneyService journeyService)
        {
            _journeyService = journeyService;
        }

        // ---- formatting helpers ---------------------------------------------------------------

        private static string FormatIso(DateTime dt)
        {
            // Normalize to UTC ISO-8601. If Kind is Unspecified, treat as UTC.
            var utc = dt.Kind == DateTimeKind.Unspecified
                ? DateTime.SpecifyKind(dt, DateTimeKind.Utc)
                : dt.ToUniversalTime();
            return utc.ToString("O");
        }

        private static string FormatIsoOrEmpty(DateTime dt)
            => dt == default ? "" : FormatIso(dt);

        // CSV escape + guard against Excel formula injection
        private static string CsvSafe(string? s)
        {
            if (string.IsNullOrEmpty(s)) return "";
            s = s.Replace("\"", "\"\""); // escape quotes
            // Guard against =, +, -, @ at start (Excel formula injection)
            if (s.Length > 0 && (s[0] == '=' || s[0] == '+' || s[0] == '-' || s[0] == '@'))
                s = "'" + s;
            var needsQuotes = s.IndexOfAny(new[] { ',', '"', '\n', '\r' }) >= 0;
            return needsQuotes ? $"\"{s}\"" : s;
        }

        // Put a clickable link into a cell using the HYPERLINK() formula (works on all ClosedXML versions)
        private static void SetHyperlinkFormula(IXLCell cell, string? url)
        {
            if (string.IsNullOrWhiteSpace(url))
            {
                cell.Value = "";
                return;
            }
            var safe = url.Replace("\"", "\"\"");
            cell.SetFormulaA1($"HYPERLINK(\"{safe}\",\"{safe}\")");
        }

        // ---- CSV ------------------------------------------------------------------------------

        public async Task<ExportResult> ExportJourneyCsvAsync(
            Guid campaignSendLogId,
            CancellationToken ct = default)
        {
            var dto = await _journeyService.GetJourneyEventsAsync(campaignSendLogId, ct);

            var sb = new StringBuilder(8 * 1024);
            sb.AppendLine("Timestamp,Source,EventType,Title,Details,Url,StepName,ButtonIndex,ButtonTitle,TemplateName,CampaignType,FlowName,FlowId,CampaignId,ContactId,ContactPhone");

            // ContactId may be non-nullable Guid in your DTO; emit empty when Guid.Empty
            string contactIdCsv = dto.ContactId == Guid.Empty ? "" : dto.ContactId.ToString();

            foreach (var e in dto.Events.OrderBy(x => x.Timestamp))
            {
                var line = string.Join(",",
                    CsvSafe(FormatIsoOrEmpty(e.Timestamp)),
                    CsvSafe(e.Source),
                    CsvSafe(e.EventType),
                    CsvSafe(e.Title),
                    CsvSafe(e.Details),
                    CsvSafe(e.Url),
                    CsvSafe(e.StepName),
                    e.ButtonIndex.HasValue ? e.ButtonIndex.Value.ToString() : "",
                    CsvSafe(e.ButtonTitle),
                    CsvSafe(e.TemplateName),
                    CsvSafe(dto.CampaignType),
                    CsvSafe(dto.FlowName),
                    dto.FlowId?.ToString() ?? "",
                    dto.CampaignId.ToString(),
                    contactIdCsv,
                    CsvSafe(dto.ContactPhone)
                );
                sb.AppendLine(line);
            }

            return new ExportResult
            {
                Bytes = Encoding.UTF8.GetBytes(sb.ToString()),
                ContentType = "text/csv; charset=utf-8",
                FileName = $"journey-{campaignSendLogId}.csv"
            };
        }

        // ---- XLSX -----------------------------------------------------------------------------

        public async Task<ExportResult> ExportJourneyXlsxAsync(
            Guid campaignSendLogId,
            CancellationToken ct = default)
        {
            var dto = await _journeyService.GetJourneyEventsAsync(campaignSendLogId, ct);

            using var wb = new XLWorkbook();
            var ws = wb.Worksheets.Add("Journey");

            var headers = new[]
            {
                "Timestamp","Source","EventType","Title","Details","Url","StepName",
                "ButtonIndex","ButtonTitle","TemplateName","CampaignType","FlowName",
                "FlowId","CampaignId","ContactId","ContactPhone"
            };

            // Header row
            for (int i = 0; i < headers.Length; i++)
                ws.Cell(1, i + 1).Value = headers[i];

            var headerRange = ws.Range(1, 1, 1, headers.Length);
            headerRange.Style.Font.Bold = true;
            headerRange.Style.Fill.BackgroundColor = XLColor.FromHtml("#F3F4F6");

            // Freeze header + auto filter
            ws.SheetView.FreezeRows(1);
            ws.Range(1, 1, 1, headers.Length).SetAutoFilter(); // avoid RangeUsed() on empty sheets

            // Data rows
            int r = 1;
            foreach (var e in dto.Events.OrderBy(x => x.Timestamp))
            {
                r++;
                int c = 1;

                ws.Cell(r, c++).Value = FormatIsoOrEmpty(e.Timestamp);
                ws.Cell(r, c++).Value = e.Source ?? "";
                ws.Cell(r, c++).Value = e.EventType ?? "";
                ws.Cell(r, c++).Value = e.Title ?? "";
                ws.Cell(r, c++).Value = e.Details ?? "";

                // URL
                SetHyperlinkFormula(ws.Cell(r, c), e.Url);
                c++;

                ws.Cell(r, c++).Value = e.StepName ?? "";
                ws.Cell(r, c++).Value = e.ButtonIndex?.ToString() ?? "";
                ws.Cell(r, c++).Value = e.ButtonTitle ?? "";
                ws.Cell(r, c++).Value = e.TemplateName ?? "";
                ws.Cell(r, c++).Value = dto.CampaignType ?? "";
                ws.Cell(r, c++).Value = dto.FlowName ?? "";
                ws.Cell(r, c++).Value = dto.FlowId?.ToString() ?? "";
                ws.Cell(r, c++).Value = dto.CampaignId.ToString();
                ws.Cell(r, c++).Value = (dto.ContactId == Guid.Empty ? "" : dto.ContactId.ToString());
                ws.Cell(r, c++).Value = dto.ContactPhone ?? "";
            }

            // Column sizing
            ws.Columns().AdjustToContents();
            ws.Column(1).Width = Math.Max(ws.Column(1).Width, 28); // timestamp column

            using var ms = new MemoryStream();
            wb.SaveAs(ms);
            var bytes = ms.ToArray();

            return new ExportResult
            {
                Bytes = bytes,
                ContentType = "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
                FileName = $"journey-{campaignSendLogId}.xlsx"
            };
        }
    }
}

using Microsoft.EntityFrameworkCore;
using System.Linq.Expressions;
using xbytechat.api.Features.Tracking.DTOs;
using xbytechat.api.Shared;

namespace xbytechat.api.Features.Tracking.Services
{


    // Strongly-typed intermediate row for EF translation (avoid 'dynamic')
    internal sealed class MessageLogRow
    {
        public Guid Id { get; set; }
        public Guid BusinessId { get; set; }
        public Guid? CampaignId { get; set; }
        public string? CampaignName { get; set; }

        public string? RecipientNumber { get; set; }
        public string? SenderId { get; set; }           // Campaign.PhoneNumberId
        public string? SourceChannel { get; set; }      // Campaign.Provider OR MessageLog.Provider
        public string? Status { get; set; }

        public string? MessageContent { get; set; }
        public string? ProviderMessageId { get; set; }
        public string? ErrorMessage { get; set; }

        public DateTime CreatedAt { get; set; }
        public DateTime? SentAt { get; set; }
    }

    public sealed class MessageLogsReportService : IMessageLogsReportService
    {
        private readonly AppDbContext _db;
        public MessageLogsReportService(AppDbContext db) => _db = db;

        public async Task<PaginatedResponse<MessageLogListItemDto>> SearchAsync(
            Guid businessId, MessageLogReportQueryDto q, CancellationToken ct)
        {
            // normalize paging
            q.Page = Math.Max(1, q.Page);
            q.PageSize = Math.Clamp(q.PageSize, 1, 200);

            // Base + left join to Campaign to enrich
            var baseQuery =
                from m in _db.MessageLogs.AsNoTracking()
                where m.BusinessId == businessId
                join c0 in _db.Campaigns.AsNoTracking() on m.CampaignId equals c0.Id into cj
                from c in cj.DefaultIfEmpty()
                select new MessageLogRow
                {
                    Id = m.Id,
                    BusinessId = m.BusinessId,
                    CampaignId = m.CampaignId,
                    CampaignName = c != null ? c.Name : null,
                    RecipientNumber = m.RecipientNumber,
                    SenderId = c != null ? c.PhoneNumberId : null,
                    SourceChannel = (c != null && c.Provider != null) ? c.Provider : m.Provider,
                    Status = m.Status,
                    MessageContent = m.MessageContent,
                    ProviderMessageId = m.ProviderMessageId ?? m.MessageId,
                    ErrorMessage = m.ErrorMessage,
                    CreatedAt = m.CreatedAt,
                    SentAt = m.SentAt
                };

            // Time window (prefer SentAt over CreatedAt)
            if (q.FromUtc.HasValue)
                baseQuery = baseQuery.Where(x => (x.SentAt ?? x.CreatedAt) >= q.FromUtc.Value);
            if (q.ToUtc.HasValue)
                baseQuery = baseQuery.Where(x => (x.SentAt ?? x.CreatedAt) <= q.ToUtc.Value);

            // Optional scope
            if (q.CampaignId.HasValue)
                baseQuery = baseQuery.Where(x => x.CampaignId == q.CampaignId.Value);

            // Status filter
            if (q.Statuses is { Length: > 0 })
            {
                var statuses = q.Statuses.Where(s => !string.IsNullOrWhiteSpace(s)).ToArray();
                if (statuses.Length > 0) baseQuery = baseQuery.Where(x => statuses.Contains(x.Status!));
            }

            // Channel filter (campaign.Provider preferred, else message.Provider)
            if (q.Channels is { Length: > 0 })
            {
                var chans = q.Channels.Select(s => s.Trim().ToUpperInvariant())
                                      .Where(s => s.Length > 0).ToArray();
                if (chans.Length > 0)
                    baseQuery = baseQuery.Where(x => x.SourceChannel != null &&
                                                     chans.Contains(x.SourceChannel.ToUpper()));
            }

            // SenderIds ‚Üí Campaign.PhoneNumberId
            if (q.SenderIds is { Length: > 0 })
            {
                var senders = q.SenderIds.Select(s => s.Trim())
                                         .Where(s => s.Length > 0).ToArray();
                if (senders.Length > 0)
                    baseQuery = baseQuery.Where(x => x.SenderId != null && senders.Contains(x.SenderId));
            }

            // Free-text search
            if (!string.IsNullOrWhiteSpace(q.Search))
            {
                var s = q.Search.Trim().ToLower();
                baseQuery = baseQuery.Where(x =>
                    (x.RecipientNumber ?? "").ToLower().Contains(s) ||
                    (x.MessageContent ?? "").ToLower().Contains(s) ||
                    (x.ErrorMessage ?? "").ToLower().Contains(s) ||
                    (x.ProviderMessageId ?? "").ToLower().Contains(s) ||
                    (x.CampaignName ?? "").ToLower().Contains(s));
            }

            // ----- Strongly-typed sorting -----
            var sortBy = (q.SortBy ?? "SentAt").Trim();
            var sortDir = (q.SortDir ?? "desc").Trim().ToLower() == "asc" ? "asc" : "desc";

            var sortMap = new Dictionary<string, Expression<Func<MessageLogRow, object>>>(
                StringComparer.OrdinalIgnoreCase)
            {
                ["Recipient"] = x => x.RecipientNumber ?? "",
                ["SenderId"] = x => x.SenderId ?? "",
                ["Channel"] = x => x.SourceChannel ?? "",
                ["Status"] = x => x.Status ?? "",
                ["CampaignName"] = x => x.CampaignName ?? "",
                ["CreatedAt"] = x => x.CreatedAt,
                ["SentAt"] = x => x.SentAt ?? x.CreatedAt
            };

            if (!sortMap.ContainsKey(sortBy)) sortBy = "SentAt";
            var keySelector = sortMap[sortBy];

            IOrderedQueryable<MessageLogRow> ordered =
                sortDir == "asc" ? baseQuery.OrderBy(keySelector)
                                 : baseQuery.OrderByDescending(keySelector);

            var total = await ordered.CountAsync(ct);

            var items = await ordered
                .Skip((q.Page - 1) * q.PageSize)
                .Take(q.PageSize)
                .Select(x => new MessageLogListItemDto
                {
                    Id = x.Id,
                    BusinessId = x.BusinessId,
                    CampaignId = x.CampaignId,
                    CampaignName = x.CampaignName,
                    RecipientNumber = x.RecipientNumber,
                    SenderId = x.SenderId,
                    SourceChannel = x.SourceChannel,
                    Status = x.Status,
                    MessageType = null,             // not on MessageLog (can be enriched later)
                    MessageContent = x.MessageContent,
                    TemplateName = null,             // not on MessageLog
                    ProviderMessageId = x.ProviderMessageId,
                    ErrorMessage = x.ErrorMessage,
                    CreatedAt = x.CreatedAt,
                    SentAt = x.SentAt,
                    DeliveredAt = null,             // not on MessageLog
                    ReadAt = null              // not on MessageLog
                })
                .ToListAsync(ct);

            return new PaginatedResponse<MessageLogListItemDto>
            {
                Items = items,
                TotalCount = total,
                Page = q.Page,
                PageSize = q.PageSize
            };
        }
        public async Task<MessageLogFacetsDto> GetFacetsAsync(Guid businessId, DateTime? fromUtc, CancellationToken ct)
        {
            // scope last 90 days by default to keep it fast
            var from = fromUtc ?? DateTime.UtcNow.AddDays(-90);

            // base query limited by business & time
            var baseQ = _db.MessageLogs
                .AsNoTracking()
                .Where(m => m.BusinessId == businessId &&
                            (m.SentAt ?? m.CreatedAt) >= from);

            // SenderIds come from Campaign.PhoneNumberId (if available)

            var senderIds = await _db.WhatsAppPhoneNumbers
            .AsNoTracking()
            .Where(w => w.BusinessId == businessId && w.WhatsAppBusinessNumber != null)
            .Select(w => w.WhatsAppBusinessNumber!)
            .Distinct()
            .OrderBy(x => x)
            .ToArrayAsync(ct);

            //var senderIds = await (
            //    from m in baseQ
            //    join c0 in _db.Campaigns.AsNoTracking() on m.CampaignId equals c0.Id into cj
            //    from c in cj.DefaultIfEmpty()
            //    where c != null && c.PhoneNumberId != null
            //    select c.PhoneNumberId!
            //)
            //.Distinct()
            //.OrderBy(x => x)
            //.ToArrayAsync(ct);

            // Channels = provider (prefer campaign.provider, else message.provider)
            var channels = await (
                from m in baseQ
                join c0 in _db.Campaigns.AsNoTracking() on m.CampaignId equals c0.Id into cj
                from c in cj.DefaultIfEmpty()
                let prov = (c != null && c.Provider != null) ? c.Provider : m.Provider
                where prov != null
                select prov!
            )
            .Distinct()
            .OrderBy(x => x)
            .ToArrayAsync(ct);

            // Statuses
            var statuses = await baseQ
                .Where(m => m.Status != null)
                .Select(m => m.Status!)
                .Distinct()
                .OrderBy(x => x)
                .ToArrayAsync(ct);

            // WABA Ids = WhatsAppBusinessNumber for this business
            var wabaIds = await _db.WhatsAppSettings
                .AsNoTracking()
                .Where(w => w.BusinessId == businessId && w.WabaId != null)
                .Select(w => w.WabaId!)
                .Distinct()
                .OrderBy(x => x)
                .ToArrayAsync(ct);

            return new MessageLogFacetsDto
            {
                WabaIds = wabaIds,
                SenderIds = senderIds,
                Channels = channels,
                Statuses = statuses
            };
        }
    }
}

using System;
using System.Threading.Tasks;
using xbytechat.api.Features.Tracking.DTOs;
using xbytechat.api.Features.Tracking.Models;
using xbytechat.api.Shared.TrackingUtils;
using Serilog;
using Microsoft.EntityFrameworkCore;
using xbytechat.api.Features.CampaignModule.DTOs;
using AutoMapper;
using xbytechat.api.Features.MessageManagement.DTOs;
using xbytechat.api.Helpers;
using xbytechat.api.Features.CampaignTracking.Models;
using xbytechat.api.Features.CRM.Dtos;

namespace xbytechat.api.Features.Tracking.Services
{
    public class TrackingService : ITrackingService
    {
        private readonly AppDbContext _context;
        private readonly IMapper _mapper;
        public TrackingService(AppDbContext context, IMapper mapper)
        {
            _context = context;
            _mapper = mapper;
        }


        public async Task<ResponseResult> LogCTAClickWithEnrichmentAsync(TrackingLogDto dto)
        {
            try
            {
                // üß† 1. Try enrich from MessageLog (if msg ID provided)
                if (!string.IsNullOrEmpty(dto.MessageId))
                {
                    var messageLog = await _context.MessageLogs
                        .FirstOrDefaultAsync(m => m.MessageId == dto.MessageId || m.Id.ToString() == dto.MessageId);

                    if (messageLog != null)
                    {
                        dto.BusinessId = dto.BusinessId == Guid.Empty ? messageLog.BusinessId : dto.BusinessId;
                        dto.ContactId ??= messageLog.ContactId;
                        dto.CampaignId ??= messageLog.CampaignId;
                        dto.MessageLogId ??= messageLog.Id;
                    }
                }

                // üß© 2. Enrich from CampaignSendLog if sourceType = campaign
                CampaignSendLog? sendLog = null;

                if (dto.SourceType == "campaign")
                {
                    if (!string.IsNullOrEmpty(dto.MessageId))
                    {
                        sendLog = await _context.CampaignSendLogs
                            .Include(c => c.Recipient)
                            .FirstOrDefaultAsync(c => c.MessageId == dto.MessageId);
                    }

                    if (sendLog == null && dto.CampaignId != null)
                    {
                        sendLog = await _context.CampaignSendLogs
                            .Include(c => c.Recipient)
                            .Where(c => c.CampaignId == dto.CampaignId)
                            .OrderByDescending(c => c.CreatedAt)
                            .FirstOrDefaultAsync();
                    }

                    if (sendLog != null)
                    {
                        dto.BusinessId = dto.BusinessId == Guid.Empty
                            ? sendLog.Recipient?.BusinessId ?? Guid.Empty
                            : dto.BusinessId;

                        dto.ContactId ??= sendLog.ContactId;
                        dto.CampaignId ??= sendLog.CampaignId;
                        dto.CampaignSendLogId ??= sendLog.Id;

                        // ‚úÖ Update campaign send log click details
                        sendLog.IsClicked = true;
                        sendLog.ClickedAt = dto.ClickedAt ?? DateTime.UtcNow;
                        sendLog.ClickType = dto.ButtonText;

                        await _context.SaveChangesAsync();
                    }
                }

                // ‚úÖ 3. Validate business context
                if (dto.BusinessId == Guid.Empty)
                {
                    Log.Warning("‚ùå TrackingLog failed: No BusinessId available for click.");
                    return ResponseResult.ErrorInfo("Business context is missing.");
                }

                // üíæ 4. Save tracking log (inline instead of separate method)
                var trackingLog = new TrackingLog
                {
                    Id = Guid.NewGuid(),
                    BusinessId = dto.BusinessId,
                    ContactId = dto.ContactId,
                    ContactPhone = dto.ContactPhone,
                    SourceType = dto.SourceType,
                    SourceId = dto.SourceId,
                    ButtonText = dto.ButtonText,
                    CTAType = dto.CTAType,
                    MessageId = dto.MessageId,
                    TemplateId = dto.TemplateId,
                    MessageLogId = dto.MessageLogId,
                    ClickedVia = dto.ClickedVia ?? "web",
                    Referrer = dto.Referrer,
                    ClickedAt = dto.ClickedAt ?? DateTime.UtcNow,
                    IPAddress = dto.IPAddress,
                    DeviceType = dto.DeviceType,
                    Browser = dto.Browser,
                    Country = dto.Country,
                    City = dto.City,
                    FollowUpSent = false,
                    LastInteractionType = "Clicked",
                    SessionId = Guid.TryParse(dto.SessionId, out var sid) ? sid : (Guid?)null,
                    ThreadId = Guid.TryParse(dto.ThreadId, out var tid) ? tid : (Guid?)null,
                    CampaignId = dto.CampaignId,
                    CampaignSendLogId = dto.CampaignSendLogId
                };

                await _context.TrackingLogs.AddAsync(trackingLog);
                await _context.SaveChangesAsync();

                // ‚úÖ Return the TrackingLog.Id in the response
                return ResponseResult.SuccessInfo("CTA click tracked successfully.", trackingLog.Id);
            }
            catch (Exception ex)
            {
                Log.Error(ex, "‚ùå Exception during CTA tracking enrichment");
                return ResponseResult.ErrorInfo("Exception during tracking: " + ex.Message);
            }
        }

        public async Task LogCTAClickAsync(TrackingLogDto dto)
        {
            try
            {
                var log = new TrackingLog
                {
                    Id = Guid.NewGuid(),
                    BusinessId = dto.BusinessId,
                    ContactId = dto.ContactId,
                    ContactPhone = dto.ContactPhone,
                    SourceType = dto.SourceType,
                    SourceId = dto.SourceId,
                    ButtonText = dto.ButtonText,
                    CTAType = dto.CTAType,
                    MessageId = dto.MessageId,
                    TemplateId = dto.TemplateId,
                    MessageLogId = dto.MessageLogId,
                    ClickedVia = dto.ClickedVia ?? "web",
                    Referrer = dto.Referrer,
                    ClickedAt = dto.ClickedAt ?? DateTime.UtcNow,
                    IPAddress = dto.IPAddress,
                    DeviceType = dto.DeviceType,
                    Browser = dto.Browser,
                    Country = dto.Country,
                    City = dto.City,
                    FollowUpSent = false,
                    LastInteractionType = "Clicked",
                    SessionId = Guid.TryParse(dto.SessionId, out var sid) ? sid : (Guid?)null,
                    ThreadId = Guid.TryParse(dto.ThreadId, out var tid) ? tid : (Guid?)null,
                    CampaignId = dto.CampaignId,
                    CampaignSendLogId = dto.CampaignSendLogId
                };

                await _context.TrackingLogs.AddAsync(log);
                await _context.SaveChangesAsync();
            }
            catch (Exception ex)
            {
                Log.Error(ex, "‚ùå Failed to log CTA click");
            }
        }
        public async Task<TrackingLogDetailsDto?> GetLogDetailsAsync(Guid logId)
        {
            var tracking = await _context.TrackingLogs
                .AsNoTracking()
                .FirstOrDefaultAsync(x => x.Id == logId);

            if (tracking == null)
                return null;

            var contact = tracking.ContactId != null
                ? await _context.Contacts.AsNoTracking().FirstOrDefaultAsync(c => c.Id == tracking.ContactId)
                : null;

            var campaign = tracking.CampaignId != null
                ? await _context.Campaigns.AsNoTracking().FirstOrDefaultAsync(c => c.Id == tracking.CampaignId)
                : null;

            var messageLog = tracking.MessageLogId != null
                ? await _context.MessageLogs.AsNoTracking().FirstOrDefaultAsync(m => m.Id == tracking.MessageLogId)
                : null;

            return new TrackingLogDetailsDto
            {
                Tracking = _mapper.Map<TrackingLogDto>(tracking),
                Contact = contact != null ? _mapper.Map<ContactDto>(contact) : null,
                Campaign = campaign != null ? _mapper.Map<CampaignDto>(campaign) : null,
                MessageLog = messageLog != null ? _mapper.Map<MessageLogDto>(messageLog) : null
            };
        }
        public async Task<List<TrackingLog>> GetFlowClickLogsAsync(Guid businessId)
        {
            return await _context.TrackingLogs
                .Where(x => x.BusinessId == businessId && x.SourceType == "cta-flow")
                .OrderByDescending(x => x.ClickedAt)
                .ToListAsync();
        }
    }
